CREATE TABLE Countries (
IdCountries int IDENTITY(1,1),
Descripcion nvarchar(50),
PRIMARY KEY (IdCountries)
);

CREATE TABLE Cities (
IdCities int IDENTITY(1,1),
Descripcion nvarchar(50),
PRIMARY KEY (IdCities)
);

CREATE TABLE HistoricalWeather (
IdCities int IDENTITY(1,1),
Temperature FLOAT,
thermalSensation FLOAT,
dateAndTime DATATIME,
IdCountries int,
PRIMARY KEY (IdCities)
);

ALTER TABLE Cities ADD FOREIGN KEY ("IdCountries") REFERENCES Countries("IdCountries");
ALTER TABLE HistoricalWeather ADD FOREIGN KEY (IdCities) REFERENCES Cities(IdCities);